# Cleaned: normalized whitespace; preserved dataclass field order and signatures.
from __future__ import annotations

from dataclasses import dataclass, field

from .tree import Instance
from .datatypes import CFrame, Enum

""" To be autogenerated in the future """

@dataclass
class AnimationClip(Instance):
    GuidBinaryString: bytes = b""
    Loop: bool = True
    Priority: Enum = 2

@dataclass
class KeyframeSequence(AnimationClip):
    AuthoredHipHeight: float = 2

@dataclass
class Keyframe(Instance):
    Time: float = 0

@dataclass
class PoseBase(Instance):
    EasingDirection: Enum = 0
    EasingStyle: Enum = 0
    Weight: float = 1

@dataclass
class Pose(PoseBase):
    CFrame: CFrame = field(default_factory=CFrame)

